unit.class:
public class DefaultValue<T>
{
    private final T originalValue;
    private T currentValue;

    public DefaultValue(T value)
    {
        originalValue = value;
        currentValue = value;
    }

    public T value()
    {
        return currentValue;
    }

    public void set(T i)
    {
        currentValue = i;
    }

    public void reset()
    {
        currentValue = originalValue;
    }

    public boolean isModified()
    {
        return originalValue != currentValue;
    }
}
