unit.interface:
public interface IncomingStream
{

    /**
     * Read in the stream data.
     */
    void read(DataInputPlus inputPlus, int version) throws IOException;

    String getName();
    long getSize();
    int getNumFiles();
    TableId getTableId();

    /**
     * @return stream session used to receive given file
     */
    StreamSession session();
}
