  public CornellBox() {
    updateGeometry(new Point3(-1.0F, -1.0F, -1.0F), new Point3(1.0F, 1.0F, 1.0F));
    this.left = new Color(0.8F, 0.25F, 0.25F);
    this.right = new Color(0.25F, 0.25F, 0.8F);
    Color color = new Color(0.7F, 0.7F, 0.7F);
