    if (boundingBox.intersects(paramBoundingBox)) {
      if (!boundingBox.contains(new Point3((paramBoundingBox.getMinimum()).x, (paramBoundingBox.getMinimum()).y, (paramBoundingBox.getMinimum()).z)))
        return true; 
      if (!boundingBox.contains(new Point3((paramBoundingBox.getMinimum()).x, (paramBoundingBox.getMinimum()).y, (paramBoundingBox.getMaximum()).z)))
        return true; 
      if (!boundingBox.contains(new Point3((paramBoundingBox.getMinimum()).x, (paramBoundingBox.getMaximum()).y, (paramBoundingBox.getMinimum()).z)))
        return true; 
      if (!boundingBox.contains(new Point3((paramBoundingBox.getMinimum()).x, (paramBoundingBox.getMaximum()).y, (paramBoundingBox.getMaximum()).z)))
        return true; 
      if (!boundingBox.contains(new Point3((paramBoundingBox.getMaximum()).x, (paramBoundingBox.getMinimum()).y, (paramBoundingBox.getMinimum()).z)))
        return true; 
      if (!boundingBox.contains(new Point3((paramBoundingBox.getMaximum()).x, (paramBoundingBox.getMinimum()).y, (paramBoundingBox.getMaximum()).z)))
        return true; 
      if (!boundingBox.contains(new Point3((paramBoundingBox.getMaximum()).x, (paramBoundingBox.getMaximum()).y, (paramBoundingBox.getMinimum()).z)))
        return true; 
