    float f2;
    int i = paramShadingState.getPrimitiveID();
    Color color = null;
    switch (i) {
      case 0:
        color = this.left;
        break;
      case 1:
        color = this.right;
        break;
      case 3:
        color = this.back;
        break;
      case 4:
        color = this.bottom;
        break;
      case 5:
        f1 = (paramShadingState.getPoint()).x;
        f2 = (paramShadingState.getPoint()).y;
        if (f1 >= this.lxmin && f1 < this.lxmax && f2 >= this.lymin && f2 < this.lymax && (paramShadingState.getRay()).dz > 0.0F)
          return; 
        color = this.top;
        break;
      default:
        assert false;
        break;
    } 
    if (Vector3.dot(paramShadingState.getNormal(), paramShadingState.getRay().getDirection()) > 0.0F) {
      paramShadingState.getNormal().negate();
      paramShadingState.getGeoNormal().negate();
    } 
    paramShadingState.storePhoton(paramShadingState.getRay().getDirection(), paramColor, color);
    double d1 = color.getAverage();
    double d2 = paramShadingState.getRandom(0, 0, 1);
    if (d2 < d1) {
      paramColor.mul(color).mul(1.0F / (float)d1);
      OrthoNormalBasis orthoNormalBasis = OrthoNormalBasis.makeFromW(paramShadingState.getNormal());
