  public void getSamples(ShadingState paramShadingState) {
    if (this.lightBounds.contains(paramShadingState.getPoint()) && (paramShadingState.getPoint()).z < this.maxZ) {
      byte b1 = (paramShadingState.getDiffuseDepth() > 0) ? 1 : this.samples;
      float f = this.area / b1;
      for (byte b2 = 0; b2 < b1; b2++) {
        double d1 = paramShadingState.getRandom(b2, 0);
        double d2 = paramShadingState.getRandom(b2, 1);
        Point3 point3 = new Point3();
        point3.x = (float)(this.lxmin * (1.0D - d1) + this.lxmax * d1);
        point3.y = (float)(this.lymin * (1.0D - d2) + this.lymax * d2);
        point3.z = this.maxZ - 0.001F;
        LightSample lightSample = new LightSample();
        lightSample.setShadowRay(new Ray(paramShadingState.getPoint(), point3));
        float f1 = lightSample.dot(paramShadingState.getNormal());
        if (f1 <= 0.0F)
          return; 
        float f2 = (lightSample.getShadowRay()).dz;
        if (f2 > 0.0F) {
          float f3 = lightSample.getShadowRay().getMax();
          float f4 = f2 / f3 * f3;
          float f5 = f4 * f;
          lightSample.setRadiance(this.radiance, this.radiance);
          lightSample.getDiffuseRadiance().mul(f5);
          lightSample.getSpecularRadiance().mul(f5);
          lightSample.traceShadow(paramShadingState);
          paramShadingState.addSample(lightSample);
        } 
      } 
    } 
  }

