public class Hair implements PrimitiveList, Shader {
  private int numSegments = 1;

  private float[] points = null;

  private ParameterList.FloatParameter widths = new ParameterList.FloatParameter(1.0F);

  public int getNumPrimitives() {
    return this.numSegments * this.points.length / 3 * (this.numSegments + 1);
  }

  public float getPrimitiveBound(int paramInt1, int paramInt2) {
    int i = paramInt1 / this.numSegments;
    int j = paramInt1 % this.numSegments;
    int k = i * (this.numSegments + 1) + j;
    int m = i * 3 * (this.numSegments + 1);
    int n = m + j * 3;
    int i1 = n + 3;
    int i2 = paramInt2 >>> 1;
    return ((paramInt2 & 0x1) == 0) ? Math.min(this.points[n + i2] - 0.5F * getWidth(k), this.points[i1 + i2] - 0.5F * getWidth(k + 1)) : Math.max(this.points[n + i2] + 0.5F * getWidth(k), this.points[i1 + i2] + 0.5F * getWidth(k + 1));
  }

  public BoundingBox getWorldBounds(Matrix4 paramMatrix4) {
    BoundingBox boundingBox = new BoundingBox();
    byte b1 = 0;
    for (byte b2 = 0; b1 < this.points.length; b2++) {
      float f = 0.5F * getWidth(b2);
      boundingBox.include(this.points[b1] - f, this.points[b1 + 1] - f, this.points[b1 + 2] - f);
      boundingBox.include(this.points[b1] + f, this.points[b1 + 1] + f, this.points[b1 + 2] + f);
      b1 += 3;
    } 
    if (paramMatrix4 != null)
      boundingBox = paramMatrix4.transform(boundingBox); 
    return boundingBox;
  }

  private float getWidth(int paramInt) {
    switch (this.widths.interp) {
      case NONE:
        return this.widths.data[0];
      case VERTEX:
        return this.widths.data[paramInt];
    } 
    return 0.0F;
  }

  private Vector3 getTangent(int paramInt1, int paramInt2, float paramFloat) {
    Vector3 vector31 = new Vector3(this.points[paramInt2 + 3] - this.points[paramInt2 + 0], this.points[paramInt2 + 4] - this.points[paramInt2 + 1], this.points[paramInt2 + 5] - this.points[paramInt2 + 2]);
    vector31.normalize();
    if (paramInt1 == 0 || paramInt1 == this.numSegments - 1)
      return vector31; 
    if (paramFloat <= 0.5F) {
      Vector3 vector3 = new Vector3(this.points[paramInt2 + 0] - this.points[paramInt2 - 3], this.points[paramInt2 + 1] - this.points[paramInt2 - 2], this.points[paramInt2 + 2] - this.points[paramInt2 - 1]);
      vector3.normalize();
      float f6 = paramFloat + 0.5F;
      float f7 = 1.0F - f6;
      float f8 = vector3.x * f7 + vector31.x * f6;
      float f9 = vector3.y * f7 + vector31.y * f6;
      float f10 = vector3.z * f7 + vector31.z * f6;
      return new Vector3(f8, f9, f10);
    } 
    paramInt2 += 3;
    Vector3 vector32 = new Vector3(this.points[paramInt2 + 3] - this.points[paramInt2 + 0], this.points[paramInt2 + 4] - this.points[paramInt2 + 1], this.points[paramInt2 + 5] - this.points[paramInt2 + 2]);
    vector32.normalize();
    float f1 = 1.5F - paramFloat;
    float f2 = 1.0F - f1;
    float f3 = vector32.x * f2 + vector31.x * f1;
    float f4 = vector32.y * f2 + vector31.y * f1;
    float f5 = vector32.z * f2 + vector31.z * f1;
    return new Vector3(f3, f4, f5);
  }

  public void intersectPrimitive(Ray paramRay, int paramInt, IntersectionState paramIntersectionState) {
    int i = paramInt / this.numSegments;
    int j = paramInt % this.numSegments;
    int k = i * 3 * (this.numSegments + 1);
    int m = k + j * 3;
    int n = m + 3;
    float f1 = this.points[n + 0] - this.points[m + 0];
    float f2 = this.points[n + 1] - this.points[m + 1];
    float f3 = this.points[n + 2] - this.points[m + 2];
    float f4 = paramRay.dy * f3 - paramRay.dz * f2;
    float f5 = paramRay.dz * f1 - paramRay.dx * f3;
    float f6 = paramRay.dx * f2 - paramRay.dy * f1;
    float f7 = f5 * f3 - f6 * f2;
    float f8 = f6 * f1 - f4 * f3;
    float f9 = f4 * f2 - f5 * f1;
    float f10 = 1.0F / (f7 * paramRay.dx + f8 * paramRay.dy + f9 * paramRay.dz);
    float f11 = f7 * (this.points[m + 0] - paramRay.ox) + f8 * (this.points[m + 1] - paramRay.oy) + f9 * (this.points[m + 2] - paramRay.oz);
    float f12 = f11 * f10;
    if (paramRay.isInside(f12)) {
      int i1 = i * (this.numSegments + 1) + j;
      float f13 = paramRay.ox + f12 * paramRay.dx;
      float f14 = paramRay.oy + f12 * paramRay.dy;
      float f15 = paramRay.oz + f12 * paramRay.dz;
      float f16 = f13 - this.points[m + 0];
      float f17 = f14 - this.points[m + 1];
      float f18 = f15 - this.points[m + 2];
      float f19 = (f1 * f16 + f2 * f17 + f3 * f18) / (f1 * f1 + f2 * f2 + f3 * f3);
      if (f19 <= 0.0F) {
        if (j == 0)
          return; 
        float f20 = this.points[m + 0] - f13;
        float f21 = this.points[m + 1] - f14;
        float f22 = this.points[m + 2] - f15;
        float f23 = f20 * f20 + f21 * f21 + f22 * f22;
        float f24 = getWidth(i1);
        if (f23 < f24 * f24 * 0.25F) {
          paramRay.setMax(f12);
          paramIntersectionState.setIntersection(paramInt, 0.0F, 0.0F);
        } 
      } else if (f19 >= 1.0F) {
        float f20 = this.points[n + 0] - f13;
        float f21 = this.points[n + 1] - f14;
        float f22 = this.points[n + 2] - f15;
        float f23 = f20 * f20 + f21 * f21 + f22 * f22;
        float f24 = getWidth(i1 + 1);
        if (f23 < f24 * f24 * 0.25F) {
          paramRay.setMax(f12);
          paramIntersectionState.setIntersection(paramInt, 0.0F, 1.0F);
        } 
      } else {
        float f20 = this.points[m + 0] + f19 * f1 - f13;
        float f21 = this.points[m + 1] + f19 * f2 - f14;
        float f22 = this.points[m + 2] + f19 * f3 - f15;
        float f23 = f20 * f20 + f21 * f21 + f22 * f22;
        float f24 = (1.0F - f19) * getWidth(i1) + f19 * getWidth(i1 + 1);
        if (f23 < f24 * f24 * 0.25F) {
          paramRay.setMax(f12);
          paramIntersectionState.setIntersection(paramInt, 0.0F, f19);
        } 
      } 
    } 
  }

  public void prepareShadingState(ShadingState paramShadingState) {
    paramShadingState.init();
    Instance instance = paramShadingState.getInstance();
    paramShadingState.getRay().getPoint(paramShadingState.getPoint());
    Ray ray = paramShadingState.getRay();
    Shader shader = instance.getShader(0);
    paramShadingState.setShader((shader != null) ? shader : this);
    int i = paramShadingState.getPrimitiveID();
    int j = i / this.numSegments;
    int k = i % this.numSegments;
    int m = j * 3 * (this.numSegments + 1);
    int n = m + k * 3;
    Vector3 vector3 = getTangent(k, n, paramShadingState.getV());
    vector3 = instance.transformVectorObjectToWorld(vector3);
    paramShadingState.setBasis(OrthoNormalBasis.makeFromWV(vector3, new Vector3(-ray.dx, -ray.dy, -ray.dz)));
    paramShadingState.getBasis().swapVW();
    paramShadingState.getNormal().set(0.0F, 0.0F, 1.0F);
    paramShadingState.getBasis().transform(paramShadingState.getNormal());
    paramShadingState.getGeoNormal().set(paramShadingState.getNormal());
    paramShadingState.getUV().set(0.0F, (k + paramShadingState.getV()) / this.numSegments);
  }

  public boolean update(ParameterList paramParameterList, SunflowAPI paramSunflowAPI) {
    this.numSegments = paramParameterList.getInt("segments", this.numSegments);
    if (this.numSegments < 1) {
      UI.printError(UI.Module.HAIR, "Invalid number of segments: %d", new Object[] { Integer.valueOf(this.numSegments) });
      return false;
    } 
    ParameterList.FloatParameter floatParameter1 = paramParameterList.getPointArray("points");
    if (floatParameter1 != null)
      if (floatParameter1.interp != ParameterList.InterpolationType.VERTEX) {
        UI.printError(UI.Module.HAIR, "Point interpolation type must be set to \"vertex\" - was \"%s\"", new Object[] { floatParameter1.interp.name().toLowerCase() });
      } else {
        this.points = floatParameter1.data;
      }  
    if (this.points == null) {
      UI.printError(UI.Module.HAIR, "Unabled to update hair - vertices are missing", new Object[0]);
      return false;
    } 
    paramParameterList.setVertexCount(this.points.length / 3);
    ParameterList.FloatParameter floatParameter2 = paramParameterList.getFloatArray("widths");
    if (floatParameter2 != null)
      if (floatParameter2.interp == ParameterList.InterpolationType.NONE || floatParameter2.interp == ParameterList.InterpolationType.VERTEX) {
        this.widths = floatParameter2;
      } else {
        UI.printWarning(UI.Module.HAIR, "Width interpolation type %s is not supported -- ignoring", new Object[] { floatParameter2.interp.name().toLowerCase() });
      }  
    return true;
  }

  public Color getRadiance(ShadingState paramShadingState) {
    paramShadingState.initLightSamples();
    paramShadingState.initCausticSamples();
    Vector3 vector31 = paramShadingState.getRay().getDirection();
    vector31.negate();
    Vector3 vector32 = new Vector3();
    Vector3 vector33 = paramShadingState.getBasis().transform(new Vector3(0.0F, 1.0F, 0.0F));
    Color color1 = Color.black();
    Color color2 = Color.black();
    for (LightSample lightSample : paramShadingState) {
      Vector3 vector3 = lightSample.getShadowRay().getDirection();
      float f1 = Vector3.dot(vector33, vector3);
      float f2 = (float)Math.sqrt((1.0F - f1 * f1));
      color1.madd(f2, lightSample.getDiffuseRadiance());
      Vector3.add(vector31, vector3, vector32);
      vector32.normalize();
      float f3 = Vector3.dot(vector33, vector32);
      float f4 = (float)Math.sqrt((1.0F - f3 * f3));
      float f5 = (float)Math.pow(f4, 10.0D);
      color2.madd(f5, lightSample.getSpecularRadiance());
    } 
    Color color3 = Color.add(color1, color2, new Color());
    return Color.blend(color3, paramShadingState.traceTransparency(), paramShadingState.getV(), new Color());
  }

  public void scatterPhoton(ShadingState paramShadingState, Color paramColor) {}

  public PrimitiveList getBakingPrimitives() {
    return null;
  }
